var Data = require('../EHR_Json/data.json')

module.exports={
    login : function (browser) {
        browser 
        .login(Data.user1[1].username, Data.user1[1].password)

},
'view files' : function (browser){
      browser
      .pause(1000)      
       .useXpath()
       .click("//*[@id='mainMenu']/ul/li[2]/a")
       .pause(500)
       .click("//*[@id='mainMenu']/ul/li[2]/ul/li[1]/a")
       .waitForElementVisible("//*[@id='page']/header/div/div/div/h1" , 60000)
       .pause(200)
       .click("//*[@id='patient']/tbody/tr/td[6]/a/i")
       .waitForElementVisible("//*[@id='mainContent']/div[8]/ul/li[1]/a" , 60000)
       .pause(500)
     },
'Hippa Notice of Privacy Practices':function(browser){
  browser
    .useXpath()
    .click("//*[@id='formtab']/a")
    .waitForElementVisible("//*[@id='formsContent']/div/div/a[text()='Confidential Patient Information']" , 60000)
    .pause(500)
    .getText("//*[@id='formsContent']/div[3]/div[2]/span" , function(result){
            browser
            if(result.value=="Completed"){
                   browser
                   .getText("//*[@id='formsContent']/div[5]/div[2]/span/span" , function(result){
                        browser
                        .click("//*[@id='formsContent']/div/div/a[text()='Hippa Notice of Privacy Practices']")
                        if(result.value=="Please Complete"){
                            browser
                               .hippaerror()
                        }
                              
                   })
                   .useXpath()
                   .pause(500)
                   
                    .hippa()

            }
        })
    browser
     .end()
    }  
  }  