var Data = require('../EHR_Json/data.json')

module.exports={
    login : function (browser) {
        browser 
        .login(Data.user1[1].username, Data.user1[1].password)

},
'view files' : function (browser){
      browser
      .pause(1000)      
       .useCss()
       .click('a[class="firstLevel"]')
       .pause(500)
       .click('a[href="/EHR/Clinicadmin/view-patient-details-open"]')
       .waitForElementVisible('#col0_filter' , 60000)
       .useXpath()
       .pause(200)
       .click("//*[@id='patient']/tbody/tr/td[6]/a/i")
       .useCss()
       .waitForElementVisible('#patient_first_name' , 60000)
       .pause(500)
},
'Admin Forms':function(browser){
	
	browser
	.useCss()
	.click('a[onclick="getAdminDefaultFormList();"]')
	.waitForElementVisible('button[onclick="getFormsList()"]' , 60000)
	.pause(1000)
	.useXpath()
	.getText("//*[@id='adminDefaultFormsContent']/div[4]/div[2]/span/span" , function(result){
		browser
		  .useXpath()
		  .click("//*[@id='adminDefaultFormsContent']/div/div/a[text()='X-RAY Release']")
		  .useCss()
		  .waitForElementVisible('#btnExportAdminForm' , 60000)
		  .pause(500)
		  if(result.value=="Please Complete"){
		  	browser
		  	.pause(1500)
		  	.getLocationInView('#backToTop')
		  	.pause(1500)
		  	  .getLocationInView('#acceptstatement_id')
		  	  .pause(1500)
		  	  .click('button[class="btn btn-primary requestRecHidden uncompletedVisible"][type="submit"]')
		  	  .pause(500)
		  	  .verify.visible('#borrowedDate_id_error')
		  	  .verify.visible('#dueDate_id_error')
		  	  .verify.visible('#acceptstatement_id_error')
		  }
		  })
		  browser
		    .pause(1000)
		    .getLocationInView('#backToTop')
		    .pause(1000)
		    .getLocationInView('#btnExportAdminForm')
		    .pause(1000)
		    .useXpath()
		    .click("//*[@id='datetimepickerxrayborrowed']/span")
		    .pause(500)
		    .click("//*[@id='datetimepickerxrayborrowed']/span")
		    .pause(500)
		    .click("//*[@id='datetimepickerxraydue']/span")
		    .pause(500)
		    .click("//*[@id='datetimepickerxraydue']/span")
		    .pause(1000)
		    .useCss()
		    .getLocationInView('#backToTop')
		    .pause(1000)
            .getLocationInView('#dueDate_id')
		    .pause(1000)
		    .click('#acceptstatement_id')
		    .pause(1000)
		    .click('button[class="btn btn-primary requestRecHidden uncompletedVisible"][type="submit"]')
		    .useXpath()
		    .waitForElementVisible("//*[@id='adminDefaultFormsContent']/div/div/a[text()='Health Insurance Verification']" , 60000)
		    .pause(1000)
		    .click("//*[@id='adminDefaultFormsContent']/div/div/a[text()='X-RAY Release']")
		    .useCss()
		    .waitForElementVisible('#btnExportAdminForm' , 60000)
		    .pause(500)
		    .verify.visible('input[id="acceptstatement_id"][checked="checked"]')
		    .pause(500)
		    .end()
		    
	
}
}	