'Fill Form':function(browser){	   
	   painIntensity=Math.floor((Math.random()*6)+1)
personalCare=Math.floor((Math.random()*6)+1)
lifting=Math.floor((Math.random()*6)+1)
reading=Math.floor((Math.random()*5)+1)
headaches=Math.floor((Math.random()*6)+1)
concentration=Math.floor((Math.random()*6)+1)
work=Math.floor((Math.random()*6)+1)
driving=Math.floor((Math.random()*6)+1)
sleeping=Math.floor((Math.random()*6)+1)
recreation=Math.floor((Math.random()*5)+1)
	browser
     .useCss()
     .waitForElementVisible('button[onclick="getDefaultFormList()"]' , 60000)
     .pause(500)
     .getLocationInView('#liftingconveniently_id')
     .pause(500)
     .getLocationInView('#_id')
     .pause(500)
     .click('input[name="painIntensity"][value="'+painIntensity+'"]')
     .click('input[name="personalCare"][value="'+personalCare+'"]')
     .click('input[name="lifting"][value="'+lifting+'"]')
     .pause(500)
     .getLocationInView('#concentrationfullyno_id')
     .pause(500)
     .getLocationInView('#liftingcan_id')
     .pause(500)
     .click('input[name="reading"][value="'+reading+'"]')
     .click('input[name="headaches"][value="'+headaches+'"]')
     .click('input[name="concentration"][value="'+concentration+'"]')
     .pause(500)
     .getLocationInView('#sleepingno_id')
     .pause(500)
     .getLocationInView('#concentrationgreat_id')
     .pause(500)
     .click('input[name="work"][value="'+work+'"]')
     .click('input[name="driving"][value="'+driving+'"]')
     .click('input[name="sleeping"][value="'+sleeping+'"]')
     .pause(500)
     .getLocationInView('#statementDivId')
     .pause(500)
     .getLocationInView('#sleepinggreatly_id')
     .pause(500)
     .click('input[name="recreation"][value="'+recreation+'"]')
     .click('#acceptstatement_id')
     .pause(300)
     .click('button[class="btn btn-primary hidden-print requestRecHidden uncompletedVisible"][type="submit"]')
     .waitForElementVisible('a[onclick="getDefaultFormList();"]' , 60000)
	     .pause(500)
	     .useXpath()
         .pause(500)
         .verify.elementNotPresent("//*[@id='formsContent']/div/div/a[text()='Neck Disability Questionnaire']")
         .pause(500)
         .useCss()
         .click('a[href="/EHR/EHR/j_spring_security_logout"]')
         .waitForElementVisible('#username_id' , 60000)
         .pause(500)
},
